document.addEventListener('DOMContentLoaded', function() {
    var form = document.getElementById('existingStudentForm');
    if (!form) return;

    var isArabic = form.dataset.lang === 'ar';
    console.log('‚úÖ Existing Student Form initialized, Language:', isArabic ? 'Arabic' : 'English');

    // ÿ•ÿ∏Ÿáÿßÿ±/ÿ•ÿÆŸÅÿßÿ° ÿ≠ŸÇŸÑ ÿ•ÿ≥ÿπÿßÿØ
    var esaadCheckbox = document.getElementById('esaad_discount');
    var esaadFileSection = document.getElementById('esaad_file_section');

    if (esaadCheckbox && esaadFileSection) {
        esaadCheckbox.addEventListener('change', function() {
            if (this.checked) {
                esaadFileSection.style.display = 'block';
            } else {
                esaadFileSection.style.display = 'none';
            }
        });
    }

    // ŸÖÿπÿßŸÑÿ¨ÿ© ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑŸÅŸàÿ±ŸÖ
    form.addEventListener('submit', function(e) {
        e.preventDefault();

        var studentId = form.dataset.studentId;
        var clubId = form.dataset.clubId;

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ™ÿ±ŸÖ
        var selectedTerm = document.querySelector('input[name="term_id"]:checked');
        if (!selectedTerm) {
            alert(isArabic ? 'Ÿäÿ¨ÿ® ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿ™ÿ±ŸÖ' : 'Please select a term');
            return;
        }

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÅ ÿßŸÑÿØÿ±ÿßÿ≥Ÿä
        var gradeSelect = document.getElementById('student_grade_id');
        if (gradeSelect && !gradeSelect.value) {
            alert(isArabic ? 'Ÿäÿ¨ÿ® ÿßÿÆÿ™Ÿäÿßÿ± ÿßŸÑÿµŸÅ ÿßŸÑÿØÿ±ÿßÿ≥Ÿä' : 'Please select the grade');
            return;
        }

        // ÿ™ÿ¨ŸáŸäÿ≤ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        var requestData = {
            student_id: parseInt(studentId),
            club_id: parseInt(clubId),
            term_id: parseInt(selectedTerm.value),
            esaad_discount: esaadCheckbox ? esaadCheckbox.checked : false
        };

        // ÿ•ÿ∂ÿßŸÅÿ© ÿßŸÑÿµŸÅ ÿßŸÑÿØÿ±ÿßÿ≥Ÿä
        if (gradeSelect && gradeSelect.value) {
            requestData.student_grade_id = parseInt(gradeSelect.value);
        }

        // ÿØÿßŸÑÿ© ÿ•ÿ±ÿ≥ÿßŸÑ ÿßŸÑÿ∑ŸÑÿ®
        function sendRequest(data) {
            var submitBtn = form.querySelector('button[type="submit"]');
            submitBtn.disabled = true;
            submitBtn.innerHTML = ' ' + (isArabic ? 'ÿ¨ÿßÿ±Ÿä ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ...' : 'Registering...');

            fetch('/registration/submit/club/existing', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    jsonrpc: "2.0",
                    method: "call",
                    params: data,
                    id: Date.now()
                })
            })
            .then(function(response) {
                return response.json();
            })
            .then(function(data) {
                console.log('Response:', data);

                if (data.error) {
                    alert(data.error.data ? data.error.data.message : (isArabic ? 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£' : 'Error occurred'));
                    submitBtn.disabled = false;
                    submitBtn.innerHTML = '' + (isArabic ? 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ' : 'Confirm Registration');
                    return;
                }

                var result = data.result;

                if (!result.success) {
                    alert(result.error || (isArabic ? 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ' : 'Registration error'));
                    submitBtn.disabled = false;
                    submitBtn.innerHTML = '' + (isArabic ? 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ' : 'Confirm Registration');
                    return;
                }

                // ÿßŸÑÿ™Ÿàÿ¨ŸäŸá ÿ≠ÿ≥ÿ® ÿßŸÑŸÜÿ™Ÿäÿ¨ÿ©
                if (result.payment_url) {
                    window.location.href = result.payment_url;
                } else if (result.needs_review) {
                    window.location.href = '/registration/pending/club/' + result.registration_id;
                } else {
                    window.location.href = '/registration/success/club/' + result.registration_id;
                }
            })
            .catch(function(error) {
                console.error('Error:', error);
                alert(isArabic ? 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ' : 'Connection error');
                submitBtn.disabled = false;
                submitBtn.innerHTML = '' + (isArabic ? 'ÿ™ÿ£ŸÉŸäÿØ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ' : 'Confirm Registration');
            });
        }

        // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ŸÖŸÑŸÅ ÿ•ÿ≥ÿπÿßÿØ
        var esaadFile = document.getElementById('esaad_card_file');
        if (esaadCheckbox && esaadCheckbox.checked && esaadFile && esaadFile.files.length > 0) {
            var file = esaadFile.files[0];
            var reader = new FileReader();

            reader.onload = function(e) {
                var base64 = e.target.result.split(',')[1];
                requestData.esaad_card_file = base64;
                requestData.esaad_card_filename = file.name;
                sendRequest(requestData);
            };

            reader.readAsDataURL(file);
        } else {
            sendRequest(requestData);
        }
    });
});

