t.script:
function switchToEnglish() {
    document.cookie = "frontend_lang=en_US;path=/";
    window.location.href = '/registration';
}

function switchToArabic() {
    document.cookie = "frontend_lang=ar_001;path=/";
    window.location.href = '/registration';
}

function switchToEnglish() {
    document.cookie = "frontend_lang=en_US;path=/;SameSite=Lax";
    window.location.href = '/registration?lang=en_US';
}

